import React, {FunctionComponent} from 'react';
import Link from "next/link";
import { Card, CardMedia, CardContent, CardHeader } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles';

interface Details {
    name: string,
    attribute: string
    role: string,
    assets?: string,
    rarity: number
}

const useStyles = makeStyles((theme) => ({
    cardWidth: {
        minWidth: 350,
        margin: "auto",
        alignContent: "center",
        alignItems: "center",
        justifyItems: "center",
        justifyContent: "center"

    },
    media: {
        // marginTop: 10,
        width: "100%",
        height: 275,
        objectFit: "contain"
        // height: 0,
        // paddingTop: '56.25%', // 16:9,
        // marginTop:'30'
    },
    content: {
        background: "orange"
    }
}))


const HeroesDetail: React.FC <Details> = ({name, attribute, role, assets, rarity}) => {
   const classes = useStyles()
    return (
        <Link href="/hero/[id]" as={`/hero/${name.toLowerCase()}`}>
            <Card className={classes.cardWidth}>
                <CardHeader title={name}/>
                <CardMedia
                    className={classes.media}
                    image={assets}
                />
                <CardContent className={classes.content} >

                    <p> Type: {attribute}</p>
                    <p> Class: {role === "manauser" ? "soul weaver" : role}</p>
                    <p> Rarity: {rarity} stars</p>
                </CardContent>
            </Card>
        </Link>
    )
}


export default HeroesDetail
