);

const Homepage: React.FC = () => {
    const classes = useStyles()
    return (
        <>
        <CssBaseline/>
        <Container className={classes.root} >
          <img src="../../img/epicSevenBg.png" alt=""/>
            <Link href="/epicSeven">
                <a>Epic Seven Heroes</a>
            </Link>
        </Container>
        </>
    )
}



export default Homepage
